package looping;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*for(int i=1;i<=10;i++) {
			
			System.out.println(i);
		}*/
		
		
		/*print even numbers
		
		for (int i=2;i<=10;i+=2)
		{
			System.out.println(i);
		}*/
		
		/*print odd numbers
		
		for(int i=1;i<=10;i+=2) {
			System.out.println(i);
		}*/
		
		
		boolean flag = true;
		
		if(flag) {
			
			for(int i = 1;i<=10;i++) {
				System.out.println(i);
			}
		}
		else {
			for(int i = 10;i>=1;i--) {
				System.out.println(i);
			}
		}

	}

}
